import React, { useState, useEffect } from 'react';

function Section8({ formData, updateFormData, onNext, onPrev }) {
  const [analysis, setAnalysis] = useState({
    hasWhen: false,
    hasWhere: false,
    hasWhatDoing: false,
    hasHowHappened: false,
    hasWhyCause: false,
    hasConsequence: false
  });

  const [aiRecommendations, setAiRecommendations] = useState([]);
  const [showAnalysis, setShowAnalysis] = useState(false);

  // Analyze description when it changes
  useEffect(() => {
    if (formData.accidentDescription && formData.accidentDescription.length > 20) {
      analyzeDescription(formData.accidentDescription);
      setShowAnalysis(true);
    } else {
      setShowAnalysis(false);
    }
  }, [formData.accidentDescription]);

  const analyzeDescription = (description) => {
    const lowerDesc = description.toLowerCase();

    // Check for time indicators
    const hasWhen = /(\d{1,2}:\d{2}|godzin|okoÅ‚o|o\s+\d|rano|wieczÃ³r|po poÅ‚udniu|dnia|w dniu|podczas|w trakcie|w momencie gdy)/i.test(description);

    // Check for location indicators
    const hasWhere = /(w miejscu|na stanowisku|w pomieszczeniu|w hali|w biurze|na|w|przy|obok|znajduje siÄ™|zlokalizow|lokalizacja|obszar|miejsce|strefa)/i.test(description);

    // Check for activity description
    const hasWhatDoing = /(wykonywaÅ‚|wykonywaÅ‚a|pracowaÅ‚|pracowaÅ‚a|zajmowaÅ‚ siÄ™|zajmowaÅ‚a siÄ™|obsÅ‚ugiwaÅ‚|obsÅ‚ugiwaÅ‚a|przygotowywaÅ‚|przygotowywaÅ‚a|realizowaÅ‚|realizowaÅ‚a|podczas|w trakcie)/i.test(description);

    // Check for sequence of events
    const hasHowHappened = /(nagle|nastÄ™pnie|po czym|w wyniku|wÃ³wczas|wtedy|w efekcie|potem|pÃ³Åºniej|najpierw|za chwilÄ™|niespodziewanie|nieoczekiwanie)/i.test(description);

    // Check for cause
    const hasWhyCause = /(poniewaÅ¼|dlatego Å¼e|z powodu|z uwagi|ze wzglÄ™du|spowodowane|przyczyna|wynika|na skutek|w wyniku|przez co|wobec czego)/i.test(description);

    // Check for injury/consequence
    const hasConsequence = /(uraz|obraÅ¼enie|rana|zÅ‚amanie|skrÄ™cenie|stÅ‚uczenie|uszkodzenie|bÃ³l|krwawienie|upadek|doznaÅ‚|doznaÅ‚a|w wyniku czego|co spowodowaÅ‚o|skutkowaÅ‚o)/i.test(description);

    setAnalysis({
      hasWhen,
      hasWhere,
      hasWhatDoing,
      hasHowHappened,
      hasWhyCause,
      hasConsequence
    });

    generateRecommendations({
      hasWhen,
      hasWhere,
      hasWhatDoing,
      hasHowHappened,
      hasWhyCause,
      hasConsequence
    }, description);
  };

  const generateRecommendations = (analysisResult, description) => {
    const recommendations = [];

    if (!analysisResult.hasWhen) {
      recommendations.push({
        priority: 'high',
        title: 'Brak dokÅ‚adnego czasu wypadku',
        message: 'Dodaj precyzyjnÄ… godzinÄ™ wystÄ…pienia wypadku. PrzykÅ‚ad: "O godzinie 14:30, podczas..." lub "OkoÅ‚o godziny 10:00 rano..."'
      });
    }

    if (!analysisResult.hasWhere) {
      recommendations.push({
        priority: 'high',
        title: 'NiewystarczajÄ…ce okreÅ›lenie miejsca',
        message: 'WskaÅ¼ dokÅ‚adnÄ… lokalizacjÄ™: nazwÄ™ pomieszczenia, halÄ™, stanowisko pracy lub konkretny obszar. PrzykÅ‚ad: "W hali produkcyjnej nr 2, przy stanowisku pakowania..." lub "W biurze na pierwszym piÄ™trze, w pobliÅ¼u..."'
      });
    }

    if (!analysisResult.hasWhatDoing) {
      recommendations.push({
        priority: 'high',
        title: 'Brak opisu wykonywanych czynnoÅ›ci',
        message: 'Opisz szczegÃ³Å‚owo, co dokÅ‚adnie robiÅ‚a osoba poszkodowana w momencie wypadku. PrzykÅ‚ad: "Pracownik zajmowaÅ‚ siÄ™ pakowaniem produktÃ³w na palety..." lub "Podczas obsÅ‚ugi maszyny frezujÄ…cej..."'
      });
    }

    if (!analysisResult.hasHowHappened) {
      recommendations.push({
        priority: 'medium',
        title: 'Brak sekwencji zdarzeÅ„',
        message: 'Opisz krok po kroku przebieg wypadku. UÅ¼yj sÅ‚Ã³w typu: "najpierw", "nastÄ™pnie", "nagle", "w wyniku czego". PrzykÅ‚ad: "Najpierw pracownik uniÃ³sÅ‚ paczkÄ™, nastÄ™pnie poÅ›lizgnÄ…Å‚ siÄ™ na mokrej podÅ‚odze..."'
      });
    }

    if (!analysisResult.hasWhyCause) {
      recommendations.push({
        priority: 'high',
        title: 'Nie wskazano przyczyny wypadku',
        message: 'WyjaÅ›nij, co byÅ‚o bezpoÅ›redniÄ… przyczynÄ… wypadku. PrzykÅ‚ad: "Wypadek wydarzyÅ‚ siÄ™ z powodu braku oznakowania mokrej podÅ‚ogi..." lub "Przyczyna: niewÅ‚aÅ›ciwe zabezpieczenie maszyny..."'
      });
    }

