import React, { useState, useEffect } from 'react';

function Section8({ formData, updateFormData, onNext, onPrev }) {
  const [analysis, setAnalysis] = useState({
    hasWhen: false,
    hasWhere: false,
    hasWhatDoing: false,
    hasHowHappened: false,
    hasWhyCause: false,
    hasConsequence: false
  });

  const [aiRecommendations, setAiRecommendations] = useState([]);
  const [showAnalysis, setShowAnalysis] = useState(false);

  // Analyze description when it changes
  useEffect(() => {
    if (formData.accidentDescription && formData.accidentDescription.length > 20) {
      analyzeDescription(formData.accidentDescription);
      setShowAnalysis(true);
    } else {
      setShowAnalysis(false);
    }
  }, [formData.accidentDescription]);

  const analyzeDescription = (description) => {
    const lowerDesc = description.toLowerCase();

    // Check for time indicators
    const hasWhen = /(\d{1,2}:\d{2}|godzin|około|o\s+\d|rano|wieczór|po południu|dnia|w dniu|podczas|w trakcie|w momencie gdy)/i.test(description);

    // Check for location indicators
    const hasWhere = /(w miejscu|na stanowisku|w pomieszczeniu|w hali|w biurze|na|w|przy|obok|znajduje się|zlokalizow|lokalizacja|obszar|miejsce|strefa)/i.test(description);

    // Check for activity description
    const hasWhatDoing = /(wykonywał|wykonywała|pracował|pracowała|zajmował się|zajmowała się|obsługiwał|obsługiwała|przygotowywał|przygotowywała|realizował|realizowała|podczas|w trakcie)/i.test(description);

    // Check for sequence of events
    const hasHowHappened = /(nagle|następnie|po czym|w wyniku|wówczas|wtedy|w efekcie|potem|później|najpierw|za chwilę|niespodziewanie|nieoczekiwanie)/i.test(description);

    // Check for cause
    const hasWhyCause = /(ponieważ|dlatego że|z powodu|z uwagi|ze względu|spowodowane|przyczyna|wynika|na skutek|w wyniku|przez co|wobec czego)/i.test(description);

    // Check for injury/consequence
    const hasConsequence = /(uraz|obrażenie|rana|złamanie|skręcenie|stłuczenie|uszkodzenie|ból|krwawienie|upadek|doznał|doznała|w wyniku czego|co spowodowało|skutkowało)/i.test(description);

    setAnalysis({
      hasWhen,
      hasWhere,
      hasWhatDoing,
      hasHowHappened,
      hasWhyCause,
      hasConsequence
    });

    generateRecommendations({
      hasWhen,
      hasWhere,
      hasWhatDoing,
      hasHowHappened,
      hasWhyCause,
      hasConsequence
    }, description);
  };

  const generateRecommendations = (analysisResult, description) => {
    const recommendations = [];

    if (!analysisResult.hasWhen) {
      recommendations.push({
        priority: 'high',
        title: 'Brak dokładnego czasu wypadku',
        message: 'Dodaj precyzyjną godzinę wystąpienia wypadku. Przykład: "O godzinie 14:30, podczas..." lub "Około godziny 10:00 rano..."'
      });
    }

    if (!analysisResult.hasWhere) {
      recommendations.push({
        priority: 'high',
        title: 'Niewystarczające określenie miejsca',
        message: 'Wskaż dokładną lokalizację: nazwę pomieszczenia, halę, stanowisko pracy lub konkretny obszar. Przykład: "W hali produkcyjnej nr 2, przy stanowisku pakowania..." lub "W biurze na pierwszym piętrze, w pobliżu..."'
      });
    }

    if (!analysisResult.hasWhatDoing) {
      recommendations.push({
        priority: 'high',
        title: 'Brak opisu wykonywanych czynności',
        message: 'Opisz szczegółowo, co dokładnie robiła osoba poszkodowana w momencie wypadku. Przykład: "Pracownik zajmował się pakowaniem produktów na palety..." lub "Podczas obsługi maszyny frezującej..."'
      });
    }

    if (!analysisResult.hasHowHappened) {
      recommendations.push({
        priority: 'medium',
        title: 'Brak sekwencji zdarzeń',
        message: 'Opisz krok po kroku przebieg wypadku. Użyj słów typu: "najpierw", "następnie", "nagle", "w wyniku czego". Przykład: "Najpierw pracownik uniósł paczkę, następnie poślizgnął się na mokrej podłodze..."'
      });
    }

    if (!analysisResult.hasWhyCause) {
      recommendations.push({
        priority: 'high',
        title: 'Nie wskazano przyczyny wypadku',
        message: 'Wyjaśnij, co było bezpośrednią przyczyną wypadku. Przykład: "Wypadek wydarzył się z powodu braku oznakowania mokrej podłogi..." lub "Przyczyna: niewłaściwe zabezpieczenie maszyny..."'
      });
    }

    if (!analysisResult.hasConsequence) {
      recommendations.push({
        priority: 'medium',
        title: 'Brak opisu skutków wypadku w opisie',
        message: 'Wskaż, jakie urazy odniosła osoba poszkodowana bezpośrednio w wyniku wypadku. Przykład: "W wyniku upadku doznał złamania nadgarstka prawej ręki..."'
      });
    }

    // Add positive feedback if everything is good
    if (Object.values(analysisResult).every(val => val === true)) {
      recommendations.push({
        priority: 'success',
        title: 'Doskonały opis wypadku!',
        message: 'Twój opis zawiera wszystkie kluczowe elementy wymagane do prawidłowego zgłoszenia wypadku. Dokument będzie kompletny i czytelny dla służb ZUS.'
      });
    }

    setAiRecommendations(recommendations);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onNext();
  };

  const isFormValid = () => {
    return formData.accidentDate &&
           formData.accidentTime &&
           formData.accidentLocation &&
           formData.plannedStartTime &&
           formData.plannedEndTime &&
           formData.injuryType &&
           formData.accidentDescription &&
           formData.wasFirstAidGiven !== '' &&
           formData.investigatingAuthority &&
           formData.wasMachineryInvolved !== '';
  };

  const getCompletionScore = () => {
    const totalCriteria = 6;
    const metCriteria = Object.values(analysis).filter(val => val === true).length;
    return Math.round((metCriteria / totalCriteria) * 100);
  };

  return (
    <div className="section-container">
      <h2 className="section-title">Część 8: Informacja o wypadku</h2>

      <form onSubmit={handleSubmit}>
        <div className="form-row">
          <div className="form-group">
            <label className="required">Data wypadku</label>
            <input
              type="date"
              value={formData.accidentDate}
              onChange={(e) => updateFormData('accidentDate', e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <label className="required">Godzina wypadku</label>
            <input
              type="time"
              value={formData.accidentTime}
              onChange={(e) => updateFormData('accidentTime', e.target.value)}
              required
            />
          </div>
        </div>

        <div className="form-group">
          <label className="required">Miejsce wypadku</label>
          <input
            type="text"
            value={formData.accidentLocation}
            onChange={(e) => updateFormData('accidentLocation', e.target.value)}
            placeholder="Wprowadź miejsce wypadku"
            required
          />
        </div>

        <div className="form-row">
          <div className="form-group">
            <label className="required">Planowana godzina rozpoczęcia pracy</label>
            <input
              type="time"
              value={formData.plannedStartTime}
              onChange={(e) => updateFormData('plannedStartTime', e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <label className="required">Planowana godzina zakończenia pracy</label>
            <input
              type="time"
              value={formData.plannedEndTime}
              onChange={(e) => updateFormData('plannedEndTime', e.target.value)}
              required
            />
          </div>
        </div>

        <div className="form-group">
          <label className="required">Rodzaj doznanych urazów</label>
          <textarea
            value={formData.injuryType}
            onChange={(e) => updateFormData('injuryType', e.target.value)}
            placeholder="Opisz rodzaj doznanych urazów"
            rows="3"
            style={{
              width: '100%',
              padding: '0.75rem',
              border: '2px solid #e0e0e0',
              borderRadius: '6px',
              fontSize: '1rem',
              fontFamily: 'inherit',
              resize: 'vertical'
            }}
            required
          />
        </div>

        <div className="form-group">
          <label className="required">Szczegółowy opis okoliczności, miejsca i przyczyn wypadku</label>
          <textarea
            value={formData.accidentDescription}
            onChange={(e) => updateFormData('accidentDescription', e.target.value)}
            placeholder="Szczegółowo opisz okoliczności, miejsce i przyczyny wypadku"
            rows="5"
            style={{
              width: '100%',
              padding: '0.75rem',
              border: '2px solid #e0e0e0',
              borderRadius: '6px',
              fontSize: '1rem',
              fontFamily: 'inherit',
              resize: 'vertical'
            }}
            required
          />
        </div>

        <div className="form-group">
          <label className="required">Czy była udzielona pierwsza pomoc medyczna?</label>
          <div className="radio-group">
            <div className="radio-option">
              <input
                type="radio"
                id="first-aid-yes"
                name="wasFirstAidGiven"
                value="Tak"
                checked={formData.wasFirstAidGiven === 'Tak'}
                onChange={(e) => updateFormData('wasFirstAidGiven', e.target.value)}
              />
              <label htmlFor="first-aid-yes">Tak</label>
            </div>
            <div className="radio-option">
              <input
                type="radio"
                id="first-aid-no"
                name="wasFirstAidGiven"
                value="Nie"
                checked={formData.wasFirstAidGiven === 'Nie'}
                onChange={(e) => updateFormData('wasFirstAidGiven', e.target.value)}
              />
              <label htmlFor="first-aid-no">Nie</label>
            </div>
          </div>
        </div>

        {formData.wasFirstAidGiven === 'Tak' && (
          <div className="form-group">
            <label className="required">Nazwa i adres placówki służby zdrowia</label>
            <textarea
              value={formData.healthFacilityInfo}
              onChange={(e) => updateFormData('healthFacilityInfo', e.target.value)}
              placeholder="Wprowadź nazwę i adres placówki służby zdrowia"
              rows="2"
              style={{
                width: '100%',
                padding: '0.75rem',
                border: '2px solid #e0e0e0',
                borderRadius: '6px',
                fontSize: '1rem',
                fontFamily: 'inherit',
                resize: 'vertical'
              }}
              required
            />
          </div>
        )}

        <div className="form-group">
          <label className="required">Organ, który prowadził postępowanie w sprawie wypadku</label>
          <textarea
            value={formData.investigatingAuthority}
            onChange={(e) => updateFormData('investigatingAuthority', e.target.value)}
            placeholder="Podaj nazwę i adres organu, który prowadził postępowanie (np. policja, prokuratura)"
            rows="2"
            style={{
              width: '100%',
              padding: '0.75rem',
              border: '2px solid #e0e0e0',
              borderRadius: '6px',
              fontSize: '1rem',
              fontFamily: 'inherit',
              resize: 'vertical'
            }}
            required
          />
        </div>

        <div className="form-group">
          <label className="required">Czy wypadek powstał podczas obsługi maszyn, urządzeń?</label>
          <div className="radio-group">
            <div className="radio-option">
              <input
                type="radio"
                id="machinery-yes"
                name="wasMachineryInvolved"
                value="Tak"
                checked={formData.wasMachineryInvolved === 'Tak'}
                onChange={(e) => updateFormData('wasMachineryInvolved', e.target.value)}
              />
              <label htmlFor="machinery-yes">Tak</label>
            </div>
            <div className="radio-option">
              <input
                type="radio"
                id="machinery-no"
                name="wasMachineryInvolved"
                value="Nie"
                checked={formData.wasMachineryInvolved === 'Nie'}
                onChange={(e) => updateFormData('wasMachineryInvolved', e.target.value)}
              />
              <label htmlFor="machinery-no">Nie</label>
            </div>
          </div>
        </div>

        {formData.wasMachineryInvolved === 'Tak' && (
          <>
            <div className="form-group">
              <label className="required">
                Czy maszyna, urządzenie były sprawne i użytkowane zgodnie z zasadami producenta?
              </label>
              <textarea
                value={formData.machineryCondition}
                onChange={(e) => updateFormData('machineryCondition', e.target.value)}
                placeholder="Opisz czy maszyna/urządzenie były sprawne i w jaki sposób były użytkowane"
                rows="3"
                style={{
                  width: '100%',
                  padding: '0.75rem',
                  border: '2px solid #e0e0e0',
                  borderRadius: '6px',
                  fontSize: '1rem',
                  fontFamily: 'inherit',
                  resize: 'vertical'
                }}
                required
              />
            </div>

            <div className="form-group">
              <label className="required">Czy maszyna, urządzenie posiada atest/deklarację zgodności?</label>
              <div className="radio-group">
                <div className="radio-option">
                  <input
                    type="radio"
                    id="cert-yes"
                    name="hasCertification"
                    value="Tak"
                    checked={formData.hasCertification === 'Tak'}
                    onChange={(e) => updateFormData('hasCertification', e.target.value)}
                  />
                  <label htmlFor="cert-yes">Tak</label>
                </div>
                <div className="radio-option">
                  <input
                    type="radio"
                    id="cert-no"
                    name="hasCertification"
                    value="Nie"
                    checked={formData.hasCertification === 'Nie'}
                    onChange={(e) => updateFormData('hasCertification', e.target.value)}
                  />
                  <label htmlFor="cert-no">Nie</label>
                </div>
              </div>
            </div>

            <div className="form-group">
              <label className="required">
                Czy maszyna, urządzenie zostało wpisane do ewidencji środków trwałych?
              </label>
              <div className="radio-group">
                <div className="radio-option">
                  <input
                    type="radio"
                    id="inventory-yes"
                    name="isInInventory"
                    value="Tak"
                    checked={formData.isInInventory === 'Tak'}
                    onChange={(e) => updateFormData('isInInventory', e.target.value)}
                  />
                  <label htmlFor="inventory-yes">Tak</label>
                </div>
                <div className="radio-option">
                  <input
                    type="radio"
                    id="inventory-no"
                    name="isInInventory"
                    value="Nie"
                    checked={formData.isInInventory === 'Nie'}
                    onChange={(e) => updateFormData('isInInventory', e.target.value)}
                  />
                  <label htmlFor="inventory-no">Nie</label>
                </div>
              </div>
            </div>
          </>
        )}

        <div className="button-group">
          <button 
            type="button" 
            className="btn btn-secondary"
            onClick={onPrev}
          >
            Wstecz
          </button>
          <button 
            type="submit" 
            className="btn btn-primary"
            disabled={!isFormValid()}
          >
            Dalej
          </button>
        </div>
      </form>
    </div>
  );
}

export default Section8;
