// Komponent wizualizacji statusu LED
const StatusLED = ({ status }) => {
  const colors = {
    ok: '#039b45',
    warning: '#bcd144',
    danger: '#ff6b6b',
  };

  const color = colors[status] || '#9e9e9e';

  return (
    <div style={{
      width: '12px',
      height: '12px',
      borderRadius: '50%',
      backgroundColor: color,
      boxShadow: `0 0 6px ${color}`,
      display: 'inline-block',
      marginRight: '8px'
    }} />
  );
};

// Komponent pojedynczego wska≈∫nika
const IndicatorItem = ({ title, status, description }) => {
  const getStatusColor = (status) => {
    switch(status) {
      case 'ok': return '#039b45';
      case 'warning': return '#bcd144';
      case 'danger': return '#ff6b6b';
      default: return '#9e9e9e';
    }
  };

  const getStatusIcon = (status) => {
    switch(status) {
      case 'ok': return '‚úì';
      case 'warning': return '!';
      case 'danger': return '‚úó';
      default: return '?';
    }
  };

  return (
    <div style={{
      padding: '1rem',
      border: `2px solid ${getStatusColor(status)}`,
      borderRadius: '8px',
      backgroundColor: `${getStatusColor(status)}10`,
    }}>
      <div style={{
        display: 'flex',
        alignItems: 'center',
        marginBottom: '0.5rem',
        fontWeight: 'bold',
      }}>
        <span style={{
          width: '24px',
          height: '24px',
          borderRadius: '50%',
          backgroundColor: getStatusColor(status),
          color: 'white',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          marginRight: '0.5rem',
          fontSize: '14px',
        }}>
          {getStatusIcon(status)}
        </span>
        {title}
      </div>
      <p style={{ margin: 0, fontSize: '0.9rem', color: '#666' }}>
        {description}
      </p>
    </div>
  );
};

