import React, { useState } from 'react';
import './App.css';
import Section1 from './components/Section1';
import Section2 from './components/Section2';
import Section3 from './components/Section3';
import Section4 from './components/Section4';
import Section5 from './components/Section5';
import Section6 from './components/Section6';
import Section7 from './components/Section7';
import Section8 from './components/Section8';
import Section9 from './components/Section9';
import Section10 from './components/Section10';
import Section11 from './components/Section11';
import FeedbackSection from './components/FeedbackSection';

function App() {
  const [currentSection, setCurrentSection] = useState(1);
  const [formData, setFormData] = useState({
    // Section 1
    pesel: '',
    documentType: '',
    documentSeries: '',
    documentNumber: '',
    firstName: '',
    lastName: '',
    birthDate: '',
    birthPlace: '',
    phoneNumber: '',
    street: '',
    houseNumber: '',
    apartmentNumber: '',
    postalCode: '',
    city: '',
    country: 'Polska',
    isCorrespondenceAddress: 'Tak',

    // Section 2
    lastPolandStreet: '',
    lastPolandHouseNumber: '',
    lastPolandApartmentNumber: '',
    lastPolandPostalCode: '',
    lastPolandCity: '',
    isLastPolandCorrespondenceAddress: 'Tak',

    // Section 3
    correspondenceType: 'adres',
    corrStreet: '',
    corrHouseNumber: '',
    corrApartmentNumber: '',
    corrPostalCode: '',
    corrCity: '',
    corrCountry: 'Polska',
  });

  const updateFormData = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const nextSection = () => {
    setCurrentSection(prev => prev + 1);
  };

  const prevSection = () => {
    setCurrentSection(prev => prev - 1);
  };

  const shouldShowSection2 = formData.country && formData.country !== 'Polska';
  const shouldShowSection3 = formData.isCorrespondenceAddress === 'Nie' || 
    (shouldShowSection2 && formData.isLastPolandCorrespondenceAddress === 'Nie');

  const getTotalSections = () => {
    let total = 1;
    if (shouldShowSection2) total++;
    if (shouldShowSection3) total++;
    return total + 1; // +1 for feedback section
  };

  const renderSection = () => {
    let sectionNumber = 1;

    if (currentSection === 1) {
      return (
        <Section1 
          formData={formData} 
          updateFormData={updateFormData}
          onNext={nextSection}
        />
      );
    }

    sectionNumber++;
    if (shouldShowSection2) {
      if (currentSection === sectionNumber) {
        return (
          <Section2 
            formData={formData} 
            updateFormData={updateFormData}
            onNext={nextSection}
            onPrev={prevSection}
          />
        );
      }
      sectionNumber++;
    }

    if (shouldShowSection3) {
      if (currentSection === sectionNumber) {
        return (
          <Section3 
            formData={formData} 
            updateFormData={updateFormData}
            onNext={nextSection}
            onPrev={prevSection}
          />
        );
      }
      sectionNumber++;
    }

    // Feedback section
    if (currentSection === sectionNumber) {
      return (
        <FeedbackSection 
          formData={formData}
          onPrev={prevSection}
        />
      );
    }

    // Default case - shouldn't happen
    return null;
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>ZANT - Zgłoszenie Wypadku przy Pracy</h1>
        <div className="progress-bar">
          <div 
            className="progress-fill" 
            style={{ width: `${(currentSection / getTotalSections()) * 100}%` }}
          />
        </div>
        <p className="section-indicator">Sekcja {currentSection} z {getTotalSections()}</p>
      </header>

      <main className="main-content">
        {renderSection()}
      </main>

      <footer className="app-footer">
        <p>System do zgłaszania wypadków przy pracy - ATLAS ZANT</p>
      </footer>
    </div>
  );
}

export default App;
