import React from 'react';

function ExplanationSection3({ formData, updateFormData, onNext, onPrev }) {
  const handleSubmit = (e) => {
    e.preventDefault();
    onNext();
  };

  const isFormValid = () => {
    return formData.accidentDescription && formData.accidentDescription.length >= 50;
  };

  const getMedicalDocuments = () => {
    return formData.medicalDocuments || [];
  };

  const addMedicalDocument = () => {
    const documents = getMedicalDocuments();
    documents.push('');
    updateFormData('medicalDocuments', [...documents]);
  };

  const updateMedicalDocument = (index, value) => {
    const documents = getMedicalDocuments();
    documents[index] = value;
    updateFormData('medicalDocuments', [...documents]);
  };

  const removeMedicalDocument = (index) => {
    const documents = getMedicalDocuments();
    documents.splice(index, 1);
    updateFormData('medicalDocuments', [...documents]);
  };

  return (
    <div className="section-container">
      <h2 className="section-title">Cz 3: Opis wypadku i dokumentacja medyczna</h2>

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label className="required">Opis wypadku</label>
          <div style={{ 
            background: '#e3f2fd', 
            padding: '0.75rem', 
            borderRadius: '4px', 
            marginBottom: '0.5rem',
            fontSize: '0.85rem',
            color: '#1565c0'
          }}>
            <strong> Wskaz贸wka:</strong> Opisz szczeg贸owo:
            <ul style={{ margin: '0.5rem 0 0 1.5rem', paddingLeft: 0 }}>
              <li>Kiedy dokadnie doszo do wypadku</li>
              <li>Co robie/robia w momencie wypadku</li>
              <li>Jak przebiega wypadek (krok po kroku)</li>
              <li>Co byo przyczyn wypadku</li>
              <li>Jakie odniose/odniosa obra偶enia</li>
            </ul>
          </div>
          <textarea
            value={formData.accidentDescription}
            onChange={(e) => updateFormData('accidentDescription', e.target.value)}
            placeholder="Opisz dokadnie przebieg wypadku, okolicznoci, przyczyny i skutki. Minimum 50 znak贸w."
            rows="10"
            style={{
              width: '100%',
              padding: '0.75rem',
              border: '2px solid #e0e0e0',
              borderRadius: '6px',
              fontSize: '1rem',
              fontFamily: 'inherit',
              resize: 'vertical'
            }}
            required
          />
          <small style={{ 
            color: formData.accidentDescription?.length >= 50 ? '#4caf50' : '#666', 
            fontSize: '0.85rem', 
            marginTop: '0.25rem', 
            display: 'block' 
          }}>
            Liczba znak贸w: {formData.accidentDescription?.length || 0} / 50 (minimum)
          </small>
        </div>

        <div style={{ 
          marginTop: '2rem',
          padding: '1.5rem',
          background: '#fff9f0',
          borderRadius: '8px',
          border: '2px solid #ffa726'
        }}>
          <h3 style={{ color: '#f57c00', marginBottom: '1rem', fontSize: '1.1rem' }}>
             Dokumenty medyczne potwierdzajce uszkodzenia ciaa
          </h3>

          <p style={{ color: '#666', fontSize: '0.9rem', marginBottom: '1rem' }}>
            Wymie dokumenty medyczne, kt贸re potwierdzaj odniesione obra偶enia 
            (np. karty informacyjne ze szpitala, zawiadczenia lekarskie, wyniki bada)
          </p>

          {getMedicalDocuments().map((doc, index) => (
            <div key={index} style={{ marginBottom: '1rem' }}>
              <div style={{ display: 'flex', gap: '0.5rem', alignItems: 'start' }}>
                <div style={{ flex: 1 }}>
                  <label style={{ fontWeight: '500', color: '#333', marginBottom: '0.25rem', display: 'block' }}>
                    Dokument {index + 1}
                  </label>
                  <input
                    type="text"
                    value={doc}
                    onChange={(e) => updateMedicalDocument(index, e.target.value)}
                    placeholder="np. Karta informacyjna ze Szpitala Miejskiego, data: 10.10.2024"
                    style={{
                      width: '100%',
                      padding: '0.75rem',
                      border: '2px solid #e0e0e0',
                      borderRadius: '6px',
                      fontSize: '1rem'
                    }}
                  />
                </div>
                <button
                  type="button"
                  onClick={() => removeMedicalDocument(index)}
                  style={{
                    marginTop: '1.75rem',
                    padding: '0.75rem 1rem',
                    background: '#f44336',
                    color: 'white',
                    border: 'none',
                    borderRadius: '6px',
                    cursor: 'pointer',
                    fontSize: '0.9rem',
                    fontWeight: '600'
                  }}
                >
                  Usu
                </button>
              </div>
            </div>
          ))}

          <button
            type="button"
            onClick={addMedicalDocument}
            style={{
              padding: '0.75rem 1.5rem',
              background: '#2196f3',
              color: 'white',
              border: 'none',
              borderRadius: '6px',
              cursor: 'pointer',
              fontSize: '1rem',
              fontWeight: '600',
              marginTop: '0.5rem'
            }}
          >
            + Dodaj dokument medyczny
          </button>

          {getMedicalDocuments().length === 0 && (
            <p style={{ 
              color: '#999', 
              fontSize: '0.9rem', 
              fontStyle: 'italic', 
              marginTop: '1rem' 
            }}>
              Nie dodano jeszcze 偶adnych dokument贸w medycznych. Kliknij przycisk powy偶ej, aby doda.
            </p>
          )}
        </div>

        <div className="button-group" style={{ marginTop: '2rem' }}>
          <button 
            type="button" 
            className="btn btn-secondary"
            onClick={onPrev}
          >
            Wstecz
          </button>
          <button 
            type="submit" 
            className="btn btn-primary"
            disabled={!isFormValid()}
          >
            Dalej
          </button>
        </div>
      </form>
    </div>
  );
}

export default ExplanationSection3;
