import React, { useState, useEffect } from 'react';

function Section8({ formData, updateFormData, onNext, onPrev }) {
  const [analysis, setAnalysis] = useState({
    when: 'danger',
    where: 'danger',
    whatDoing: 'danger',
    howHappened: 'danger',
    whyCause: 'danger',
    consequence: 'danger',
    whenDesc: '',
    whereDesc: '',
    whatDoingDesc: '',
    howHappenedDesc: '',
    whyCauseDesc: '',
    consequenceDesc: ''
  });

  const [aiRecommendations, setAiRecommendations] = useState([]);
  const [showAnalysis, setShowAnalysis] = useState(false);
  const [isChecked, setIsChecked] = useState(false);
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  // Konfiguracja API URL
  const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

  // WywoÅ‚anie prawdziwego API
  const callAIAssistant = async (description) => {
    setIsAnalyzing(true);

    try {
      const response = await fetch(`${API_URL}/evaluate-injury`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ description })
      });

      if (!response.ok) {
        throw new Error(`API request failed: ${response.status}`);
      }

      const data = await response.json();

      // Mapowanie odpowiedzi z backendu do formatu uÅ¼ywanego przez frontend
      const mappedResponse = {
        aspects: [
          {
            name: 'when',
            status: data.When.Status,
            userMessage: data.When.Description
          },
          {
            name: 'where',
            status: data.Where.Status,
            userMessage: data.Where.Description
          },
          {
            name: 'what_doing',
            status: data.Doing.Status,
            userMessage: data.Doing.Description
          },
          {
            name: 'how_happened',
            status: data.How.Status,
            userMessage: data.How.Description
          },
          {
            name: 'why_cause',
            status: data.Why.Status,
            userMessage: data.Why.Description
          },
          {
            name: 'consequence',
            status: data.Injury.Status,
            userMessage: data.Injury.Description
          }
        ]
      };

      setIsAnalyzing(false);
      return mappedResponse;

    } catch (error) {
      console.error('Error calling AI assistant:', error);
      setIsAnalyzing(false);

      // Fallback do mock response w przypadku bÅ‚Ä™du
      return {
        aspects: [
          {
            name: 'when',
            status: 'danger',
            userMessage: 'Nie udaÅ‚o siÄ™ poÅ‚Ä…czyÄ‡ z serwerem AI. SprawdÅº czy backend jest uruchomiony.'
          },
          {
            name: 'where',
            status: 'danger',
            userMessage: 'Nie udaÅ‚o siÄ™ poÅ‚Ä…czyÄ‡ z serwerem AI.'
          },
          {
            name: 'what_doing',
            status: 'danger',
            userMessage: 'Nie udaÅ‚o siÄ™ poÅ‚Ä…czyÄ‡ z serwerem AI.'
          },
          {
            name: 'how_happened',
            status: 'danger',
            userMessage: 'Nie udaÅ‚o siÄ™ poÅ‚Ä…czyÄ‡ z serwerem AI.'
          },
          {
            name: 'why_cause',
            status: 'danger',
            userMessage: 'Nie udaÅ‚o siÄ™ poÅ‚Ä…czyÄ‡ z serwerem AI.'
          },
          {
            name: 'consequence',
            status: 'danger',
            userMessage: 'Nie udaÅ‚o siÄ™ poÅ‚Ä…czyÄ‡ z serwerem AI.'
          }
        ]
      };
    }
  };

  const analyzeDescription = async (description) => {
    // WywoÅ‚anie API
    const apiResponse = await callAIAssistant(description);

    // Przetworzenie odpowiedzi z API
    const analysisResult = {
      when: apiResponse.aspects.find(a => a.name === 'when')?.status || 'danger',
      where: apiResponse.aspects.find(a => a.name === 'where')?.status || 'danger',
      whatDoing: apiResponse.aspects.find(a => a.name === 'what_doing')?.status || 'danger',
      howHappened: apiResponse.aspects.find(a => a.name === 'how_happened')?.status || 'danger',
      whyCause: apiResponse.aspects.find(a => a.name === 'why_cause')?.status || 'danger',
      consequence: apiResponse.aspects.find(a => a.name === 'consequence')?.status || 'danger',
      whenDesc: apiResponse.aspects.find(a => a.name === 'when')?.userMessage || '',
      whereDesc: apiResponse.aspects.find(a => a.name === 'where')?.userMessage || '',
      whatDoingDesc: apiResponse.aspects.find(a => a.name === 'what_doing')?.userMessage || '',
      howHappenedDesc: apiResponse.aspects.find(a => a.name === 'how_happened')?.userMessage || '',
      whyCauseDesc: apiResponse.aspects.find(a => a.name === 'why_cause')?.userMessage || '',
      consequenceDesc: apiResponse.aspects.find(a => a.name === 'consequence')?.userMessage || ''
    };

    setAnalysis(analysisResult);
    generateRecommendations(apiResponse.aspects, description);
    setIsChecked(true);
  };

  const generateRecommendations = (aspects, description) => {
    const recommendations = [];

    aspects.forEach(aspect => {
      if (aspect.status === 'danger') {
        recommendations.push({
          priority: 'high',
          aspectName: aspect.name,
          title: getAspectTitle(aspect.name),
          message: aspect.userMessage
        });
      } else if (aspect.status === 'warning') {
        recommendations.push({
          priority: 'medium',
          aspectName: aspect.name,
          title: getAspectTitle(aspect.name),
          message: aspect.userMessage
        });
      }
    });

    // SprawdÅº czy wszystko jest OK
    const allOk = aspects.every(a => a.status === 'ok');
    if (allOk) {
      recommendations.push({
        priority: 'success',
        title: 'DoskonaÅ‚y opis wypadku!',
        message: 'TwÃ³j opis zawiera wszystkie kluczowe elementy wymagane do prawidÅ‚owego zgÅ‚oszenia wypadku. Dokument bÄ™dzie kompletny i czytelny dla sÅ‚uÅ¼b ZUS.'
      });
    }

    setAiRecommendations(recommendations);
  };

  const getAspectTitle = (aspectName) => {
    const titles = {
      'when': 'Brak dokÅ‚adnego czasu wypadku',
      'where': 'NiewystarczajÄ…ce okreÅ›lenie miejsca',
      'what_doing': 'Brak opisu wykonywanych czynnoÅ›ci',
      'how_happened': 'Brak sekwencji zdarzeÅ„',
      'why_cause': 'Nie wskazano przyczyny wypadku',
      'consequence': 'Brak opisu skutkÃ³w wypadku'
    };
    return titles[aspectName] || 'Uwaga';
  };

  // Reset sprawdzenia gdy uÅ¼ytkownik zmienia tekst
  useEffect(() => {
    if (formData.accidentDescription && formData.accidentDescription.length > 20) {
      setShowAnalysis(true);
      // Reset flagi sprawdzenia gdy tekst siÄ™ zmieniÅ‚
      if (isChecked) {
        setIsChecked(false);
      }
    } else {
      setShowAnalysis(false);
      setIsChecked(false);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [formData.accidentDescription]);

  const handleCheckText = async () => {
    if (formData.accidentDescription && formData.accidentDescription.length > 20) {
      await analyzeDescription(formData.accidentDescription);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // JeÅ›li tekst nie zostaÅ‚ sprawdzony, wykonaj sprawdzenie przed przejÅ›ciem dalej
    if (!isChecked && formData.accidentDescription && formData.accidentDescription.length > 20) {
      await analyzeDescription(formData.accidentDescription);

      // Po analizie sprawdÅº czy sÄ… problemy
      const score = getCompletionScore();
      if (score < 100) {
        const confirmMessage = `âš ï¸ Wykryto problemy z opisem wypadku!\n\nKompletnoÅ›Ä‡ opisu: ${score}%\n\nBrakujÄ…ce elementy mogÄ… spowodowaÄ‡ odrzucenie zgÅ‚oszenia przez ZUS.\n\nCzy na pewno chcesz przejÅ›Ä‡ dalej bez poprawienia opisu?`;

        if (!window.confirm(confirmMessage)) {
          return; // Nie przechodzimy dalej
        }
      }
    } else if (isChecked) {
      // SprawdÅº wynik jeÅ›li byÅ‚ juÅ¼ sprawdzony
      const score = getCompletionScore();
      if (score < 100) {
        const confirmMessage = `âš ï¸ Wykryto problemy z opisem wypadku!\n\nKompletnoÅ›Ä‡ opisu: ${score}%\n\nBrakujÄ…ce elementy mogÄ… spowodowaÄ‡ odrzucenie zgÅ‚oszenia przez ZUS.\n\nCzy na pewno chcesz przejÅ›Ä‡ dalej bez poprawienia opisu?`;

        if (!window.confirm(confirmMessage)) {
          return; // Nie przechodzimy dalej
        }
      }
    }

    onNext();
  };

  const isFormValid = () => {
    return formData.accidentDate &&
           formData.accidentTime &&
           formData.accidentLocation &&
           formData.plannedStartTime &&
           formData.plannedEndTime &&
           formData.injuryType &&
           formData.accidentDescription &&
           formData.wasFirstAidGiven !== '' &&
           formData.investigatingAuthority &&
           formData.wasMachineryInvolved !== '';
  };

  const getCompletionScore = () => {
    const totalCriteria = 6;
    const metCriteria = Object.values(analysis).filter(val => val === 'ok').length;
    return Math.round((metCriteria / totalCriteria) * 100);
  };

  return (
    <div className="section-container">
      <h2 className="section-title">CzÄ™Å›Ä‡ 8: Informacja o wypadku</h2>

      <form onSubmit={handleSubmit}>
        {/* BASIC INFORMATION */}
        <div style={{ 
          background: '#f8f9fa', 
          padding: '1.5rem', 
          borderRadius: '8px', 
          marginBottom: '2rem',
          border: '1px solid #e0e0e0'
        }}>
          <h3 style={{ color: '#667eea', marginBottom: '1rem', fontSize: '1.1rem' }}>
            Podstawowe informacje
          </h3>

          <div className="form-row">
            <div className="form-group">
              <label className="required">Data wypadku</label>
              <input
                type="date"
                value={formData.accidentDate}
                onChange={(e) => updateFormData('accidentDate', e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label className="required">Godzina wypadku</label>
              <input
                type="time"
                value={formData.accidentTime}
                onChange={(e) => updateFormData('accidentTime', e.target.value)}
                required
              />
            </div>
          </div>

          <div className="form-group">
            <label className="required">Miejsce wypadku</label>
            <input
              type="text"
              value={formData.accidentLocation}
              onChange={(e) => updateFormData('accidentLocation', e.target.value)}
              placeholder="WprowadÅº miejsce wypadku"
              required
            />
          </div>

          <div className="form-row">
            <div className="form-group">
              <label className="required">Planowana godzina rozpoczÄ™cia pracy</label>
              <input
                type="time"
                value={formData.plannedStartTime}
                onChange={(e) => updateFormData('plannedStartTime', e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label className="required">Planowana godzina zakoÅ„czenia pracy</label>
              <input
                type="time"
                value={formData.plannedEndTime}
                onChange={(e) => updateFormData('plannedEndTime', e.target.value)}
                required
              />
            </div>
          </div>
        </div>

        {/* INJURIES AND DESCRIPTION */}
        <div style={{ 
          background: '#fff9f0', 
          padding: '1.5rem', 
          borderRadius: '8px', 
          marginBottom: '2rem',
          border: '2px solid #ffa726'
        }}>
          <h3 style={{ color: '#f57c00', marginBottom: '1rem', fontSize: '1.1rem' }}>
            âš ï¸ Opis wypadku i urazÃ³w (kluczowa sekcja)
          </h3>

          <div className="form-group">
            <label className="required">Rodzaj doznanych urazÃ³w</label>
            <textarea
              value={formData.injuryType}
              onChange={(e) => updateFormData('injuryType', e.target.value)}
              placeholder="Opisz rodzaj doznanych urazÃ³w, np: zÅ‚amanie nadgarstka prawej rÄ™ki, gÅ‚Ä™bokie skaleczenie lewej dÅ‚oni, stÅ‚uczenie kolana..."
              rows="3"
              style={{
                width: '100%',
                padding: '0.75rem',
                border: '2px solid #ffa726',
                borderRadius: '6px',
                fontSize: '1rem',
                fontFamily: 'inherit',
                resize: 'vertical'
              }}
              required
            />
          </div>

          <div className="form-group">
            <label className="required">
              SzczegÃ³Å‚owy opis okolicznoÅ›ci, miejsca i przyczyn wypadku
            </label>
            <div style={{ 
              background: '#e3f2fd', 
              padding: '0.75rem', 
              borderRadius: '4px', 
              marginBottom: '0.5rem',
              fontSize: '0.85rem',
              color: '#1565c0'
            }}>
              <strong>ðŸ’¡ WskazÃ³wka:</strong> Dobry opis powinien zawieraÄ‡:
              <ul style={{ margin: '0.5rem 0 0 1.5rem', paddingLeft: 0 }}>
                <li>Kiedy dokÅ‚adnie doszÅ‚o do wypadku (godzina, okolicznoÅ›ci)</li>
                <li>Gdzie dokÅ‚adnie miaÅ‚o miejsce zdarzenie</li>
                <li>Co robiÅ‚a osoba poszkodowana w tym momencie</li>
                <li>Jak przebiegaÅ‚ wypadek krok po kroku</li>
                <li>Co byÅ‚o przyczynÄ… wypadku</li>
                <li>Jakie byÅ‚y skutki (urazy)</li>
              </ul>
            </div>
            <textarea
              value={formData.accidentDescription}
              onChange={(e) => updateFormData('accidentDescription', e.target.value)}
              placeholder="PrzykÅ‚ad: O godzinie 14:30 w hali produkcyjnej nr 2, przy stanowisku pakowania, pracownik zajmowaÅ‚ siÄ™ ukÅ‚adaniem paczek na palecie. Podczas podnoszenia ciÄ™Å¼kiej skrzynki (ok. 25 kg), poÅ›lizgnÄ…Å‚ siÄ™ na mokrej podÅ‚odze, poniewaÅ¼ wczeÅ›niej zostaÅ‚a ona umyta bez oznakowania. W wyniku upadku doznaÅ‚ zÅ‚amania nadgarstka prawej rÄ™ki oraz silnego stÅ‚uczenia kolana..."
              rows="8"
              style={{
                width: '100%',
                padding: '0.75rem',
                border: showAnalysis && getCompletionScore() < 100 ? '2px solid #ff9800' : '2px solid #4caf50',
                borderRadius: '6px',
                fontSize: '1rem',
                fontFamily: 'inherit',
                resize: 'vertical',
                boxShadow: showAnalysis ? '0 0 10px rgba(255, 152, 0, 0.2)' : 'none'
              }}
              required
            />
          </div>

          {/* PRZYCISK SPRAWDÅ¹ TEKST */}
          {formData.accidentDescription && formData.accidentDescription.length > 20 && (
            <div style={{ marginTop: '1rem', display: 'flex', alignItems: 'center', gap: '1rem' }}>
              <button
                type="button"
                onClick={handleCheckText}
                disabled={isAnalyzing}
                style={{
                  padding: '0.75rem 1.5rem',
                  background: isChecked ? '#4caf50' : '#2196f3',
                  color: 'white',
                  border: 'none',
                  borderRadius: '6px',
                  fontSize: '1rem',
                  fontWeight: '600',
                  cursor: isAnalyzing ? 'not-allowed' : 'pointer',
                  opacity: isAnalyzing ? 0.6 : 1,
                  transition: 'all 0.3s ease',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.5rem'
                }}
              >
                {isAnalyzing ? (
                  <>
                    <span style={{ 
                      display: 'inline-block', 
                      width: '16px', 
                      height: '16px', 
                      border: '2px solid white',
                      borderTop: '2px solid transparent',
                      borderRadius: '50%',
                      animation: 'spin 1s linear infinite'
                    }}></span>
                    AnalizujÄ™...
                  </>
                ) : isChecked ? (
                  <>âœ… Sprawdzono</>
                ) : (
                  <>ðŸ” SprawdÅº tekst</>
                )}
              </button>
            </div>
          )}

          {/* AI ANALYSIS SECTION */}
          {showAnalysis && isChecked && (
            <div style={{ marginTop: '1.5rem' }}>
              <div style={{
                background: getCompletionScore() === 100 ? '#e8f5e9' : '#fff3e0',
                border: `2px solid ${getCompletionScore() === 100 ? '#4caf50' : '#ff9800'}`,
                borderRadius: '8px',
                padding: '1.5rem'
              }}>
                <div style={{ display: 'flex', alignItems: 'center', marginBottom: '0.5rem' }}>
                  <span style={{ fontSize: '1.5rem', marginRight: '0.75rem' }}>
                    {getCompletionScore() === 100 ? 'âœ…' : 'ðŸ¤–'}
                  </span>
                  <h4 style={{ color: '#333', margin: 0 }}>
                    Analiza AI: KompletnoÅ›Ä‡ opisu ({getCompletionScore()}%)
                  </h4>
                </div>

                <p style={{ color: '#666', fontSize: '0.9rem', marginBottom: '1rem', fontStyle: 'italic' }}>
                  ðŸ’¡ UzupeÅ‚nienie brakÃ³w w opisie przyspieszy proces weryfikacji wniosku przez ZUS.
                </p>

                {/* Visual Indicators */}
                <div style={{ 
                  display: 'grid', 
                  gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', 
                  gap: '0.75rem'
                }}>
                  <IndicatorItem 
                    title="Kiedy?" 
                    status={analysis.when} 
                    description={analysis.whenDesc || 'OkreÅ›l dokÅ‚adny czas wypadku'}
                  />
                  <IndicatorItem 
                    title="Gdzie?" 
                    status={analysis.where} 
                    description={analysis.whereDesc || 'OkreÅ›l dokÅ‚adne miejsce wypadku'}
                  />
                  <IndicatorItem 
                    title="Co robiÅ‚?" 
                    status={analysis.whatDoing} 
                    description={analysis.whatDoingDesc || 'Opisz czynnoÅ›ci przed wypadkiem'}
                  />
                  <IndicatorItem 
                    title="Jak doszÅ‚o?" 
                    status={analysis.howHappened} 
                    description={analysis.howHappenedDesc || 'Opisz sekwencjÄ™ zdarzeÅ„'}
                  />
                  <IndicatorItem 
                    title="Dlaczego?" 
                    status={analysis.whyCause} 
                    description={analysis.whyCauseDesc || 'OkreÅ›l przyczynÄ™ wypadku'}
                  />
                  <IndicatorItem 
                    title="Skutki" 
                    status={analysis.consequence} 
                    description={analysis.consequenceDesc || 'Opisz obraÅ¼enia'}
                  />
                </div>
              </div>
            </div>
          )}
        </div>

        {/* OTHER INFORMATION */}
        <div style={{ 
          background: '#f8f9fa', 
          padding: '1.5rem', 
          borderRadius: '8px', 
          marginBottom: '1.5rem',
          border: '1px solid #e0e0e0'
        }}>
          <h3 style={{ color: '#667eea', marginBottom: '1rem', fontSize: '1.1rem' }}>
            Dodatkowe informacje
          </h3>

        <div className="form-group">
          <label className="required">Czy byÅ‚a udzielona pierwsza pomoc medyczna?</label>
          <div className="radio-group">
            <div className="radio-option">
              <input
                type="radio"
                id="first-aid-yes"
                name="wasFirstAidGiven"
                value="Tak"
                checked={formData.wasFirstAidGiven === 'Tak'}
                onChange={(e) => updateFormData('wasFirstAidGiven', e.target.value)}
              />
              <label htmlFor="first-aid-yes">Tak</label>
            </div>
            <div className="radio-option">
              <input
                type="radio"
                id="first-aid-no"
                name="wasFirstAidGiven"
                value="Nie"
                checked={formData.wasFirstAidGiven === 'Nie'}
                onChange={(e) => updateFormData('wasFirstAidGiven', e.target.value)}
              />
              <label htmlFor="first-aid-no">Nie</label>
            </div>
          </div>
        </div>

        {formData.wasFirstAidGiven === 'Tak' && (
          <div className="form-group">
            <label className="required">Nazwa i adres placÃ³wki sÅ‚uÅ¼by zdrowia</label>
            <textarea
              value={formData.healthFacilityInfo}
              onChange={(e) => updateFormData('healthFacilityInfo', e.target.value)}
              placeholder="WprowadÅº nazwÄ™ i adres placÃ³wki sÅ‚uÅ¼by zdrowia"
              rows="2"
              style={{
                width: '100%',
                padding: '0.75rem',
                border: '2px solid #e0e0e0',
                borderRadius: '6px',
                fontSize: '1rem',
                fontFamily: 'inherit',
                resize: 'vertical'
              }}
              required
            />
          </div>
        )}

        <div className="form-group">
          <label className="required">Organ, ktÃ³ry prowadziÅ‚ postÄ™powanie w sprawie wypadku</label>
          <textarea
            value={formData.investigatingAuthority}
            onChange={(e) => updateFormData('investigatingAuthority', e.target.value)}
            placeholder="Podaj nazwÄ™ i adres organu, ktÃ³ry prowadziÅ‚ postÄ™powanie (np. policja, prokuratura)"
            rows="2"
            style={{
              width: '100%',
              padding: '0.75rem',
              border: '2px solid #e0e0e0',
              borderRadius: '6px',
              fontSize: '1rem',
              fontFamily: 'inherit',
              resize: 'vertical'
            }}
            required
          />
        </div>

        <div className="form-group">
          <label className="required">Czy wypadek powstaÅ‚ podczas obsÅ‚ugi maszyn, urzÄ…dzeÅ„?</label>
          <div className="radio-group">
            <div className="radio-option">
              <input
                type="radio"
                id="machinery-yes"
                name="wasMachineryInvolved"
                value="Tak"
                checked={formData.wasMachineryInvolved === 'Tak'}
                onChange={(e) => updateFormData('wasMachineryInvolved', e.target.value)}
              />
              <label htmlFor="machinery-yes">Tak</label>
            </div>
            <div className="radio-option">
              <input
                type="radio"
                id="machinery-no"
                name="wasMachineryInvolved"
                value="Nie"
                checked={formData.wasMachineryInvolved === 'Nie'}
