// Helper component for visual indicators
function IndicatorItem({ title, status, description }) {
  const getStatusColor = (status) => {
    switch(status) {
      case 'ok': return '#4caf50';
      case 'warning': return '#ff9800';
      case 'danger': return '#f44336';
      default: return '#9e9e9e';
    }
  };

  const getStatusIcon = (status) => {
    switch(status) {
      case 'ok': return 'âœ“';
      case 'warning': return '!';
      case 'danger': return 'âœ—';
      default: return '?';
    }
  };

  return (
    <div style={{
      padding: '1rem',
      border: `2px solid ${getStatusColor(status)}`,
      borderRadius: '8px',
      backgroundColor: `${getStatusColor(status)}10`,
    }}>
      <div style={{
        display: 'flex',
        alignItems: 'center',
        marginBottom: '0.5rem',
        fontWeight: 'bold',
      }}>
        <span style={{
          width: '24px',
          height: '24px',
          borderRadius: '50%',
          backgroundColor: getStatusColor(status),
          color: 'white',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          marginRight: '0.5rem',
          fontSize: '14px',
        }}>
          {getStatusIcon(status)}
        </span>
        {title}
      </div>
      <p style={{ margin: 0, fontSize: '0.9rem', color: '#666' }}>
        {description}
      </p>
    </div>
  );
}

// Komponent diody LED statusu
function StatusLED({ status, label }) {
  const getColors = () => {
    switch(status) {
      case 'ok':
        return {
          bg: '#e8f5e9',
          border: '#4caf50',
          led: '#4caf50',
          text: '#2e7d32',
          animation: 'none'
        };
      case 'warning':
        return {
          bg: '#fff3e0',
          border: '#ff9800',
          led: '#ff9800',
          text: '#e65100',
          animation: 'pulse 2s ease-in-out infinite'
        };
      case 'danger':
      default:
        return {
          bg: '#ffebee',
          border: '#f44336',
          led: '#f44336',
          text: '#c62828',
          animation: 'pulse 2s ease-in-out infinite'
        };
    }
  };

  const colors = getColors();

  return (
    <div style={{
      display: 'flex',
      alignItems: 'center',
      gap: '0.3rem',
      padding: '0.3rem 0.6rem',
      background: colors.bg,
      borderRadius: '12px',
      border: `1px solid ${colors.border}`,
      fontSize: '0.75rem',
      fontWeight: '600'
    }}>
      <div style={{
        width: '8px',
        height: '8px',
        borderRadius: '50%',
        background: colors.led,
        boxShadow: `0 0 6px ${colors.led}`,
        animation: colors.animation
      }}></div>
      <span style={{ color: colors.text }}>
        {label}
      </span>
    </div>
  );
}

